package Capitulo2;

public class ModDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int iResult, iRem;
		double dResult, dRem;
		
		iResult = 10 / 3;
		iRem = 10 % 3;
		
		dResult = 10.0 / 3.0;
		dRem = 10.0 % 3.0;
		
		System.out.println("O resultado e o resto de 10 / 3: " + iResult + " " + iRem);
		
		System.out.println("O resultado e o resto de 10 / 3: " + dResult + " " + dRem);
		
	}

}
