package Capitulo2;

public class SCops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n, d, q;
		
		n = 10;
		d = 2;		
		if(d != 0 && (n % d) == 0)
			System.out.println(d + " é fator de " + n);
		
		d = 0;		
		if(d != 0 && (n % d) == 0)
			System.out.println(d + " é fator de " + n);		
		
		if(d != 0 & (n % d) == 0)
			System.out.println(d + " é fator de " + n);		

	}

}
